import { StyleSheet, Text, View, ScrollView, TouchableOpacity, TextInput } from 'react-native';
import { useCallback, useState } from "react";

export default function TodoScreen({ navigation }) {
    const [todos, SetTodos] = useState([{
        "title": "Taffer"
    }, {
        "title": "Faire les courses"
    }, {
        "title": "Manger"
    }, {
        "title": "Aller à la salle"
    }, {
        "title": "test"
    }, {
        "title": "test bis"
    }, {
        "title": "test bis 2"
    }, {
        "title": "test bis 3"
    }, {
        "title": "test bis 4"
    }]);
    const [text_input, SetTextInput] = useState("");
    const addTodo = useCallback(() => {
        console.log("let's go:", text_input, text_input.length);
        if (text_input.length > 0) {
            console.log("IF");
            SetTodos(todos.concat([{
                'title': text_input
            }]));
            SetTextInput("");
        }
    }, [text_input, todos])
    return (
        <View style={styles.container}>
            <Text style={styles.title}>Todos:</Text>
            <ScrollView>
                {
                    todos.map((todo, index) => {
                        return (
                            <TouchableOpacity key={index} style={styles.todo} onPress={() =>
                                SetTodos(todos.filter((t, i) => i != index))
                            }>
                                <Text style={styles.add_text}>{todo.title}</Text>
                            </TouchableOpacity>
                        )
                    })
                }
            </ScrollView>
            <View style={styles.form}>
                <TouchableOpacity onPress={() => navigation.goBack()}>
                    <View style={styles.addButton}>
                        <Text>Back</Text>
                    </View>
                </TouchableOpacity>
                <TextInput value={text_input} style={styles.input} onChange={event => SetTextInput(event.target.value)} onSubmitEditing={addTodo} />
                <TouchableOpacity onPress={addTodo}>
                    <View style={styles.addButton}>
                        <Text>+</Text>
                    </View>
                </TouchableOpacity>
            </View>
        </View>
    )
}

const styles = StyleSheet.create({
    container: {
        padding: 10,
        flex: 1,
        flexDirection: 'column',
        justifyContent: 'space-around'
    },
    title: {
        textAlign: 'center',
        fontSize: 24,
        fontWeight: 'bold',
    },
    todo: {
        borderRadius: 10,
        borderBottomWidth: 1,
        padding: 10,
        marginVertical: 2,
        backgroundColor: '#8AD1DB'
    },
    form: {
        flexDirection: 'row',
        justifyContent: 'center',
        alignSelf: 'center'
    },
    input: {
        borderColor: 'black',
        borderWidth: 2,
        justifyContent: 'flex-start',
        padding: 20,
        marginHorizontal: 10,
        height: 50,
        borderRadius: 10
    },
    addButton: {
        justifyContent: 'flex-end',
        justifyContent: 'center',
        textAlign: 'center',
        borderRadius: "100%",
        height: 50,
        width: 50,
        backgroundColor: '#8AD1DB'
    },
});
