import { StyleSheet, Text, View, ScrollView, TouchableOpacity, TextInput, Button } from 'react-native';
import { useState } from "react";

export default function TodoScreen({ navigation }) {
    const [todos, SetTodos] = useState([{
        "id": 1,
        "title": "Faire mes devoirs"
    }, {
        "id": 2,
        "title": "Faire les courses"
    }, {
        "id": 3,
        "title": "Chercher le courrier"
    }]);
    const [text_input, SetTextInput] = useState("");
    return (
        <View style={styles.container}>
            <Text style={styles.title}>Todos:</Text>
            <ScrollView>
                {
                    todos.map(todo => {
                        return (
                            <TouchableOpacity key={todo.id} style={styles.todo} onPress={() =>
                                SetTodos(todos.filter((t) => t.id != todo.id))
                            }>
                                <Text style={styles.add_text}>{todo.title}</Text>
                            </TouchableOpacity>
                        )
                    })
                }
            </ScrollView>
            <View style={styles.form}>
                <TouchableOpacity onPress={() => navigation.goBack()}>
                    <View style={styles.addButton}>
                        <Text>Back</Text>
                    </View>
                </TouchableOpacity>
                <TextInput value={text_input} style={styles.input} onChange={(event) => {
                    event.preventDefault();
                    SetTextInput(event.target.value);
                }}
                    onSubmitEditing={() => {
                        SetTodos(todos.concat([{
                            'id': todos.length,
                            'title': text_input
                        }]));
                        SetTextInput("");
                }} />
                <TouchableOpacity onPress={() => {
                    SetTodos(todos.concat([{
                        'id': todos.length,
                        'title': text_input
                    }]));
                    SetTextInput("");
                }}>
                    <View style={styles.addButton}>
                        <Text>+</Text>
                    </View>
                </TouchableOpacity>
            </View>
        </View>
    )
}

const styles = StyleSheet.create({
    container: {
        padding: 10,
        flex: 1,
        flexDirection: 'column',
        justifyContent: 'space-around'
    },
    title: {
        textAlign: 'center',
        fontSize: 24,
        fontWeight: 'bold',
    },
    todo: {
        borderRadius: 10,
        borderBottomWidth: 1,
        padding: 10,
        marginVertical: 2,
        backgroundColor: '#8AD1DB'
    },
    form: {
        flexDirection: 'row',
        justifyContent: 'center',
        alignSelf: 'center'
    },
    input: {
        borderColor: 'black',
        borderWidth: 2,
        justifyContent: 'flex-start',
        padding: 20,
        marginHorizontal: 10,
        height: 50,
        borderRadius: 10
    },
    addButton: {
        justifyContent: 'flex-end',
        justifyContent: 'center',
        textAlign: 'center',
        borderRadius: "100%",
        height: 50,
        width: 50,
        backgroundColor: '#8AD1DB'
    },
});
