import { StyleSheet, Text, View, FlatList, SafeAreaView, Platform, Button, TouchableHighlight } from 'react-native';
import { useEffect, useState } from "react";

export default function TodoScreen({ navigation }) {
    const [tasks, SetTasks] = useState([{
        "id": 0,
        "title": "Taffer",
        "done": true
    }, {
        "id": 1,
        "title": "Faire les courses",
        "done": false
    }]);
    const moveTask = (task) => {
        const task_index = tasks.findIndex((t) => task.id == t.id);
        const new_tasks = [...tasks];
        new_tasks[task_index].done = !task.done;
        SetTasks(new_tasks);
    };
    const renderTasks = (task, expected) => {
        if (task.done !== expected)
            return null;
        return (
            <View style={styles.task}>
                <Text style={styles.small_text}>{task.title}</Text>
                <TouchableHighlight onPress={moveTask.bind(this, task)}>
                    <View style={styles.delete_button}>
                        <Text>Move</Text>
                    </View>
                </TouchableHighlight>
            </View>
        );
    };
    useEffect(() => {
        console.log("EFFECT");
    })
    console.log("RENDER !");
    return (
        <SafeAreaView style={styles.container}>
            <View style={styles.title_section}>
                <Text style={styles.title}>Todo list</Text>
            </View>
            <View style={styles.content_section}>
                <View style={styles.tasks_section}>
                    <Text style={styles.title}>Task(s) to do:</Text>
                    <FlatList
                        data={tasks}
                        renderItem={({ item }) => renderTasks(item, false)}
                        keyExtractor={(item) => item.id}
                    />
                </View>
                <View style={styles.tasks_section}>
                    <Text style={styles.title}>Task(s) done:</Text>
                    <FlatList
                        data={tasks}
                        renderItem={({ item }) => renderTasks(item, true)}
                        keyExtractor={(item) => item.id}
                    />
                </View>
            </View>
            <View style={styles.bottom_section}>

            </View>
        </SafeAreaView>
    )
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        paddingTop: Platform.OS === 'android' ? 35 : 0,
        backgroundColor: "#D4FBFF"
    },
    title_section: {
        flex: 0.5,
        flexDirection: 'column',
        justifyContent: 'center',
        alignContent: 'center',
        backgroundColor: "#5C8C91"
    },
    title: {
        textAlign: 'center',
        fontSize: 24,
        fontWeight: 'bold',
        color: 'white'
    },
    content_section: {
        flex: 5,
        backgroundColor: "#A2D9DF"
    },
    tasks_section: {
        flex: 3
    },
    bottom_section: {
        flex: 0.5,
        backgroundColor: '#5C8C91'
    },
    task: {
        flex: 1,
        flexDirection: 'row',
        justifyContent: 'space-around',
        alignItems: 'center',
        backgroundColor: '#5C8C91',
        borderRadius: 20,
        paddingVertical: 10,
        marginBottom: 10,
    },
    move_button: {
        padding: 10,
        backgroundColor: '#D5D8D8'
    },
    delete_button: {
        padding: 10,
        backgroundColor: "#FF614F"
    },
    small_text: {
        fontSize: 12,
        textAlign: 'center',
        color: 'white',
        fontWeight: 'bold'
    }
});
