import { StyleSheet, Text, View, FlatList, SafeAreaView, Platform } from 'react-native';
import { useState } from "react";

function renderTasks(task, expected) {
    console.log("task:", task);
    console.log("expected:", expected);
    if (task.done !== expected)
        return null;
    return (
        <View>
            <Text>{task.title}</Text>
        </View>
    );
}

export default function TodoScreen({ navigation }) {
    const [tasks, SetTasks] = useState([{
        "title": "Taffer",
        "id": 0,
        "done": true
    }, {
        "title": "Faire les courses",
        "id": 1,
        "done": false
    }]);
    const [text_input, SetTextInput] = useState("");
    return (
        <SafeAreaView style={styles.container}>
            <View style={styles.title_section}>
                <Text style={styles.title}>Todo list</Text>
            </View>
            <View style={styles.content_section}>
                <View style={styles.tasks_section}>
                    <Text style={styles.title}>Task(s) to do:</Text>
                    <FlatList
                        data={tasks}
                        renderItem={(task) => renderTasks(task.item, false)}
                        keyExtractor={(item) => item.id}
                    />
                </View>
                <View style={styles.tasks_section}>
                    <Text style={styles.title}>Task(s) done:</Text>
                    <FlatList
                        data={tasks}
                        renderItem={({ item }) => renderTasks(item, true)}
                        keyExtractor={(item) => item.id}
                    />
                </View>
            </View>
            <View style={styles.bottom_section}>

            </View>
        </SafeAreaView>
    )
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        paddingTop: Platform.OS === 'android' ? 35 : 0,
        backgroundColor: "#D4FBFF"
    },
    title_section: {
        flex: 0.5,
        flexDirection: 'column',
        justifyContent: 'center',
        alignContent: 'center',
        backgroundColor: "#5C8C91"
    },
    title: {
        textAlign: 'center',
        fontSize: 24,
        fontWeight: 'bold',
        color: 'white'
    },
    content_section: {
        flex: 5,
        backgroundColor: "#A2D9DF"
    },
    tasks_section: {
        flex: 3
    },
    bottom_section: {
        flex: 0.5,
        backgroundColor: '#5C8C91'
    },
    task: {
        flex: 1,
        flexDirection: 'row',
        justifyContent: 'space-around',
        backgroundColor: '#5C8C91',
        borderRadius: 20,
        padding: 20,
        marginBottom: 10,
    },
    small_text: {
        fontSize: 12,
        textAlign: 'center',
        color: 'white',
        fontWeight: 'bold'
    }
});
