import { StyleSheet, Text, TouchableHighlight, View, Button } from 'react-native';

import { NavigationContainer } from '@react-navigation/native';

/**
 * Component used to redirect user in other screen
 * @param {String} name Name of the menu
 * @param {String} route Route to redirect on click
 * @returns 
 */
function HomeMenuButton({ navigation, name, route }) {
    return (
        <TouchableHighlight style={styles.menuButton} onPress={() => navigation.push(route)}>
            <Text>{name}</Text>
        </TouchableHighlight>
    );
}

function HomeScreen({navigation}) {
    return (
        <View style={styles.container}>
            <Text style={styles.home_title}>Welcome !</Text>
            <View style={styles.home_menu}>
                <HomeMenuButton navigation={navigation} name="Todo List" route="Home"></HomeMenuButton>
                <HomeMenuButton navigation={navigation} name="Screen A" route="Home"></HomeMenuButton>
                <HomeMenuButton navigation={navigation} name="Screen B" route="Home"></HomeMenuButton>
                <HomeMenuButton navigation={navigation} name="Screen C" route="Home"></HomeMenuButton>
                <HomeMenuButton navigation={navigation} name="Screen D" route="Home"></HomeMenuButton>
            </View>
        </View>
    );
}

function ToDoScreen({navigation}) {
    return (
        <View>
            <Button title='Back' onPress={() => navigation.goBack()}></Button>
            <Text>Todo !</Text>
        </View>
    )
}

const Stack = createNativeStackNavigator();

export default function App() {
  return (
      <NavigationContainer>
          <Stack.Navigator screenOptions={{
              headerShown: false
            }}>
              <Stack.Screen name="Home" component={HomeScreen} />
              <Stack.Screen name="Todo" component={TodoScreen} />
          </Stack.Navigator>
      </NavigationContainer>
  );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        flexDirection: 'column',
        padding: 20
    },
    home_title: {
        textAlign: 'center',
        fontSize: 20,
        fontWeight: 'bold',
        flex: 1
    },
    home_menu: {
        flex: 5,
        flexWrap: 'wrap',
        flexDirection: "row",
        justifyContent: 'center'
    },
    menuButton: {
        justifyContent: 'center',
        textAlign: 'center',
        marginHorizontal: "10%",
        width: "30%",
        height: "20%",
        borderRadius: 20,
        alignSelf: "flex-start",
        backgroundColor: "#8AD1DB",
    }
});
